import{ React, useEffect, useState } from 'react';
import { Container, Form, Button, Col } from 'react-bootstrap'
import herobanner from './assets/herobanner.png'
import axios from 'axios'
import Result from './components/Result';

const Home = () => {
  const[ crypto, setCrypto ] = useState("");
  const[ country, setCountry ] = useState("");
  const[ coinlist, setCoinlist] = useState([]);
  const[ currency, setCurrency ] = useState([]);
  const[toggle, setToggle] = useState(false)


const handleSubmit = (e) =>{
  e.preventDefault();
  if(crypto && country){
    setToggle(true)
  }
}

const handleChange = (e) =>{
  const tokenid = coinlist.filter((t)=>t.name === e.target.value)
  setCrypto(tokenid[0].id)
  }
  
const fetchCoins = async() => {
  const res = await axios.get(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false`);
  setCoinlist(res.data)
  }
 
const fetchCurrency = async() =>{
  const res = await axios.get("https://api.coingecko.com/api/v3/simple/supported_vs_currencies")
  setCurrency(res.data)

}

 useEffect(() => {
  fetchCoins();
 },[]);

 useEffect(()=>{
  fetchCurrency();
 },[])
 
  return (
    <Container>

    <Col className='inputform' >
    <h2 style={{color: "white", fontWeight:"bold"}}>Cryptocurrency Coverter</h2>
    <div className='underline'></div>

    <div className='inputfield'>
    <p style={{color:"white"}}>Select your preferred Crypto</p>
    <Form.Select aria-label="Default select example" required  onChange ={handleChange}>
      <option selected disabled hidden>Select Cryptocurrency</option>
      {coinlist && coinlist.map((t) => (
      <option key={t.id}>{t.name}</option>
    ))}
    </Form.Select>
    </div>

    <div className='inputfield'>
    <p style={{color:"white"}}>Select Global Currency</p>
    <Form.Select aria-label="Default select example" style ={{ textTransform: "Capitalize"}} onChange={(e) => setCountry(e.target.value)}>
     <option selected disabled hidden>Select Currency</option>
     {currency && currency.map((key) =>(
     <option style={{ textTransform:"uppercase"}} key ={key}>{key}</option>
    ))}
    </Form.Select>
    </div>

    <Button style={{width:"40%", marginTop:"5px"}} variant="warning" onClick ={handleSubmit}>Convert</Button>
    </Col>

    <Container>
    <img src={herobanner} alt="banner" className='herobanner' />
    </Container>

    {
     toggle && <Result crypto ={crypto} country ={country} />
    }
    
    </Container>
  
  
  )
}

export default Home
