import{ React, useEffect, useState } from 'react';
import Appbar from './components/Appbar';
import { Container, Form, Button } from 'react-bootstrap'
import herobanner from './assets/herobanner.png'
import { Coins } from './utils/fetchData';
import axios from 'axios'

const Home = () => {
  const[ crypto, setCrypto ] = useState("");
  const[ country, setCountry ] = useState("");
  const[ coinlist, setCoinlist] = useState([]);


  const fetchCoins = async() => {
    const res = await axios.get('https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false');
    setCoinlist(res.data)
  }


  useEffect(() => {
    fetchCoins();
  }, []);

  return (
    <Container>
    <div className='inputform' >
    <h2 style={{color: "white", fontWeight:"bold", marginBottom:"40px" }}>Cryptocurrency Coverter</h2>
    <div className='inputfield'>
    <p style={{color:"white"}}>Select your preferred Crypto</p>
    <Form.Select aria-label="Default select example" onChange={(e)=> setCrypto(e.target.value)}>
    <option selected disabled hidden >Select Cryptocurrency</option>
    <option value="1">One</option>
    <option value="2">Two</option>
    <option value="3">Three</option>
  </Form.Select>
  </div>
  <span className='inputfield'>
  <p style={{color:"white"}}>Select Global currency</p>
  <Form.Select aria-label="Default select example" onChange={(e) => setCountry(e.target.value)}>
    <option selected disabled hidden >Select Currency</option>
    <option value="1">One</option>
    <option value="2">Two</option>
    <option value="3">Three</option>
    </Form.Select>
    </span>
    <Button style={{width:"40%", marginTop:"5px"}} variant="warning">Convert</Button>
    </div>
    <Container>
    <img src={herobanner} alt="banner" className='herobanner' />
    </Container>
    </Container>
  
  )
}

export default Home
