import {React, useEffect, useState} from 'react';
import { Container} from 'react-bootstrap';
import axios from 'axios';



const Result = ({country}) => {
  const[ price, setPrice ] = useState([])

const fetchResult = async () => {
  const res = await axios.get(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=${country}&ids=bitcoin&order=market_cap_desc&per_page=100&page=1&sparkline=false`)
  setPrice(res.data)
}

useEffect(()=>{
  fetchResult();
},[country])


  return (
    <Container>
    <div className='result'>
    {price && price.map(({current_price, id, high_24h})=>(
      <div>
        <div style={{ fontSize:"25px"}} key ={id}>Current price: {current_price}</div>
        <div  style={{ fontSize:"18px", marginTop:"10px"}} key ={id}>Highest today: {high_24h}</div>
        </div>
    )) }
    </div>
    </Container>
  )
}

export default Result
